pragma solidity 0.5.16;

interface IERC20Helper {
    function approveToken(address tokenAddress, uint256 amount, address spender)
        external
        returns (bool);

    function transferToken(
        address tokenAddress,
        uint256 amount,
        address spender
    ) external returns (bool);

    function transferTokens(
        address[] calldata tokens,
        uint256[] calldata amounts,
        address spender
    ) external returns (bool);

    function approveTokens(
        address[] calldata tokens,
        uint256[] calldata amounts,
        address spender
    ) external returns (bool);

    function getBalancesOf(address[] calldata tokens, address owner)
        external
        view
        returns (uint256[] memory);

    function getAllowances(
        address[] calldata tokens,
        address owner,
        address spender
    ) external view returns (uint256[] memory);

    function getBalancesOfAndAllowances(
        address[] calldata tokens,
        address owner,
        address spender
    )
        external
        view
        returns (uint256[] memory balancesOf, uint256[] memory allowances);

    function mintTo(
        address[] calldata tokens,
        uint256[] calldata amounts,
        address receiver
    ) external returns (bool);

}
